package Codes;

public class Switch_Case {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 1;
		
		switch (num) {
		case 1:
			System.out.println("Hello World");
			break;
		case 2:
			System.out.println("Hello World");
			break;
		default:
			System.out.println("Non of the cases");
			break;
		}
		System.out.println("Program End");
	}

}
